import java.awt.*;import java.util.*;public class Predator extends Animal{	/**	*	Constructor creates a Predator with Position 0,0.  Animal	*	has no cage in which to live.	*/	public Predator()	{		super();	}		/**	*	Constructor creates a Predator in a random empty spot in	*	the given cage.	*	@param cage  the cage in which Predator will be created.	*/	public Predator(Cage cage)	{		super(cage);	}		/**	*	Constructor creates a Predator in a random empty spot in	*	the given cage with the specified Color.	*	@param cage  the cage in which Predator will be created.	*	@param color  the color of the Predator	*/	public Predator(Cage cage, Color color)	{		super(cage, color);	}		/**	*	Constructor creates a Predator in the given Position	*	the given cage with the specified Color.	*	@param cage  the cage in which Predator will be created.	*	@param color  the color of the Predator	*	@param pos	the position of the Predator	*/	public Predator(Cage cage, Color color, Position pos)	{		super(cage, color, pos);	}		public String toString()	{		return (myPos.toString() + " is an Predator Animal.  ");	}}