/******************************************************************** Class:  DisplayAuthor:  Greg King  Date:  December 1, 2004This class is used to graphically display the locations and movement of the Animalsin the zoo simulation.  It also displays the boundaries of the cage.Date			Modification12-01-2004	Main coding on this class*********************************************************************/import java.awt.*;public class Display extends Frame{	private Cage myCage;	private int scale;		/**	*	Constructor takes a reference to the Cage to be displayed.	*	@param cage  the Cage to be displayed	*/	public Display(Cage cage)	{		myCage = cage;		scale = 15;	}		/**	*	Method updates the Display object on the contents of the cage and	*	repaints the window.	*	@param cage  the Cage to be displayed	*/	public void refresh(Cage cage)	{		myCage = cage;		this.resize(myCage.getMax_X()*scale+30, myCage.getMax_Y()*scale+35);		this.setLocation(320,0);		this.setVisible(true);		repaint();	}		public void paint(Graphics g)	{		int x, y;		int xLen = myCage.getMax_X()*scale;		int yLen = myCage.getMax_Y()*scale;		g.setColor(Color.black);		g.drawRect(8, 23, xLen+2, yLen+2);				Animal[] list = myCage.allAnimals();		for(int n=0; n<list.length; n++)		{			g.setColor(list[n].getColor());			x = list[n].getPosition().getX();			y = list[n].getPosition().getY();			g.fillOval(x*scale+10, y*scale+25,scale/2,scale/2);		}	}}